mapper.select:
SELECT d.id, d.name, d.device_type_id, d.code, d.code_sgcc, d.room_id, d.cabinet_id, d.cabinet_position, d.manufacturer, d.brand, d.series, d.model, d.status, d.use, d.use_date, d.purchase_method, d.serial_number, d.manufacture_date, d.service_expiry_date, d.network, d.ip_address, d.mgt_dept_id, d.mgt_employee_id, d.mgt_phone, d.maintenance_supplier, d.maintenance_date_start, d.maintenance_date_end, dt.id "deviceType.id", dt.name "deviceType.name", dt.name_abbr "deviceType.nameAbbr", dt.parent_id "deviceType.parentId", dt.order_number "deviceType.orderNumber", r.id "room.id", r.name "room.name", r.number "room.number", r.place "room.place", r.type "room.type", c.id "cabinet.id", c.name "cabinet.name", c.type "cabinet.type", c.room_id "cabinet.roomId", dept.id "department.id", dept.name "department.name", dept.name_abbr "department.nameAbbr", dept.code "department.code", dept.parent_id "department.parentId", emp.id "employee.id", emp.name "employee.name", emp.code "employee.code", emp.department_id "employee.departmentId", emp.phone "employee.phone", emp.cellphone "employee.cellphone" FROM device_safety d LEFT OUTER JOIN basedata_device_type dt ON d.device_type_id = dt.id LEFT OUTER JOIN basedata_room r ON d.room_id = r.id LEFT OUTER JOIN basedata_cabinet c ON d.cabinet_id = c.id LEFT OUTER JOIN basedata_department dept ON d.mgt_dept_id = dept.id LEFT OUTER JOIN basedata_employee emp ON d.mgt_employee_id = emp.id
d.id = #{id}
AND d.name LIKE "%"#{name}"%"
AND d.device_type_id = #{deviceTypeId}
AND d.code LIKE "%"#{code}"%"
AND d.code_sgcc LIKE "%"#{codeSgcc}"%"
AND d.room_id = #{roomId}
AND d.cabinet_id = #{cabinetId}
AND d.cabinet_position LIKE "%"#{cabinetPosition}"%"
AND d.manufacturer LIKE "%"#{manufacturer}"%"
AND d.brand LIKE "%"#{brand}"%"
AND d.series LIKE "%"#{series}"%"
AND d.model LIKE "%"#{model}"%"
AND d.status = #{status}
AND d.use LIKE "%"#{use}"%"
AND d.use_date = #{useDate}
AND d.purchase_method LIKE "%"#{purchaseMethod}"%"
AND d.serial_number LIKE "%"#{serialNumber}"%"
AND d.manufacture_date = #{manufactureDate}
AND d.service_expiry_date = #{serviceExpiryDate}
AND d.network LIKE "%"#{network}"%"
AND d.ip_address LIKE "%"#{ipAddress}"%"
AND d.mgt_dept_id = #{mgtDeptId}
AND d.mgt_employee_id = #{mgtEmployeeId}
AND d.mgt_phone LIKE "%"#{mgtPhone}"%"
AND d.maintenance_supplier LIKE "%"#{maintenanceSupplier}"%"
AND d.maintenance_date_start = #{maintenanceDateStart}
AND d.maintenance_date_end = #{maintenanceDateEnd}
ORDER BY d.code ;

